package java1016;

public class Indexof2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String a="646416316165";
		
		int length=a.length();
		System.out.println(length);
		if(length==12) {
			System.out.println("입력한 주민번호 길이가 맞습니다.");
		}
		else {
			System.out.println("s");
		}
		
	}

}
